mapper.update:
UPDATE users
userPwd = #{userPwd}, name = #{name}, nickName = #{nickName}, email = #{email}, tel = #{tel}, userImage = #{userImage}, gender = #{gender}, registerWay = #{registerWay}, userStatus = #{userStatus}, level = #{level}, point = #{point}, policyFlag = #{policyFlag}, updateDate = NOW()
WHERE userId = #{userId}

update.if:
update delete_users 
set userId = CONCAT('del_', userId), 
    email = CONCAT('del_', email) 
where userId = #{userId};
delete from users where userId = #{userId};
