mapper.select:
SELECT viewIdx, userId, productIdx, viewDate 
FROM recent_view_product 
WHERE userId = #{userId} 
group by productIdx
ORDER BY viewDate DESC 
LIMIT #{limit}

SELECT searchIdx, userId, search_term as searchTerm, searchDate 
FROM recent_search 
WHERE userId = #{userId} 
ORDER BY searchDate DESC 
LIMIT #{limit}

SELECT searchIdx, userId, search_term, searchDate 
FROM recent_search 
WHERE userId = #{userId} 
ORDER BY searchDate DESC 
LIMIT #{limit}
