mapper.select:
SELECT type, id, title, link, created_at FROM ( -- 포토리뷰 가져오기 SELECT '포토리뷰' AS type, p.photoReviewIdx AS id, p.title, CONCAT('/photoReview/photoReviewContent?photoReviewIdx=', p.photoReviewIdx) AS link, p.reviewDate AS created_at FROM photoreview p WHERE p.userIdx = #{userIdx}
UNION ALL
-- 상품리뷰 가져오기 SELECT '상품리뷰' AS type, pr.reviewIdx AS id, pr.reviewContent AS title, CONCAT('/shop/productDetails?productIdx=', pr.productIdx) AS link, pr.reviewDate AS created_at FROM productreview pr WHERE pr.userId = (SELECT userId FROM users WHERE userIdx = #{userIdx}) ) AS activities ORDER BY created_at DESC;

mapper.update:
UPDATE users
userPwd = #{vo.userPwd}, name = #{vo.name}, nickName = #{vo.nickName}, gender = #{vo.gender}, email = #{vo.email}, tel = #{vo.tel}, userImage = #{vo.userImage}, registerWay = #{vo.registerWay}, updateDate = default, policyFlag = #{vo.policyFlag},
WHERE userId = #{vo.userId}

UPDATE users
userPwd = #{vo.userPwd}, name = #{vo.name}, nickName = #{vo.nickName}, gender = #{vo.gender}, email = #{vo.email}, tel = #{vo.tel}, userImage = #{vo.userImage}, registerWay = #{vo.registerWay}, policyFlag = #{vo.policyFlag}
WHERE userId = #{vo.userId}
